CREATE TABLE scripts (id INT AUTO_INCREMENT NOT NULL, name VARCHAR(255) NOT NULL, slug VARCHAR(255) NOT NULL, contents LONGTEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE tickets (id INT NOT NULL, type_id INT DEFAULT NULL, creation_time DATETIME NOT NULL, use_time DATETIME DEFAULT NULL, used TINYINT(1) NOT NULL, INDEX IDX_54469DF4C54C8C93 (type_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE ticket_types (id INT AUTO_INCREMENT NOT NULL, slug VARCHAR(255) NOT NULL, price INT NOT NULL, active TINYINT(1) NOT NULL, receiver VARCHAR(255) NOT NULL, UNIQUE INDEX UNIQ_7100EABB989D9B62 (slug), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE users (id INT AUTO_INCREMENT NOT NULL, username VARCHAR(255) NOT NULL, pin INT NOT NULL, balance INT NOT NULL, last_daily DATETIME NOT NULL, last_iron DATETIME NOT NULL, iron_today INT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE tickets ADD CONSTRAINT FK_54469DF4C54C8C93 FOREIGN KEY (type_id) REFERENCES ticket_types (id);